package day2808;

public class stringpro {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String A = "Yes";
		String B = "HOLIDAY";
		String c = "TOMORROW";
		
		System.out.println(A.length());
		
		
		
		System.out.println(A+B+c);
		System.out.println("mynameishiren".length());
		System.out.println(A.concat(B).concat(c));
		
		System.out.println(B.charAt(0));
		System.out.println(B.contains("HOL"));
		
		System.out.println(A.toUpperCase());
		System.out.println(A.toLowerCase());
		
		System.out.println(A.replace('Y', 'H'));
		
		
		System.out.println(A.equals("YES"));

		System.out.println(A.equals("YES"));
		
		System.out.println(A.equalsIgnoreCase("Yes"));	
		
		
		System.out.println(B.substring(0, 7));
		
		System.out.println(B.substring(2, 5));
		
		
		
		
		
		
	}

}
