
package star;

public class pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int i,j, rows=4;
		for(i=1;i<=rows;i++)
		{
			for(j=1;j<=rows;j++)
			{
				if(j<=rows-1)
				{
					System.out.print("l");		
				}
				
				else
					
				{
					System.out.print("*");
				}
				
			}
			
			System.out.println();
		}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
