package star;

public class pra1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    
		
	/*				 *  
		           * *
	         	 * * *
	           * * * *
		    
		
	*/	
		int row=4;
		for(int i = 1; i<=row;i++)
		{
			for(int j=1;j<=row;j++)
			{
				if(j<=row-i)
				{
					System.out.print(" ");
				}
				else
				{
					System.out.print("*");
				}
            }
                    System.out.println(); 			
		}
		
		
		
		
		
		
	 			
		
		
		
		
	}

}
